

create database MobileEstore
go

use MobileEstore
go


create table HangSX(
	MaHang  varchar(30) primary key,
	TenHang nvarchar(30),
	DaXoa bit
)
go


create table LoaiSP(
	MaLoai varchar(30) primary key,
	TenLoai nvarchar(30),
	DaXoa bit
)
go

create table CauHinh(
	MaCH int identity(1,1) primary key,
	DoPhanGiai varchar(30),
	DoRong nvarchar(80),
	HeDH varchar(30),
	Chip nvarchar(70),
	Pin varchar(30),
	Ram varchar(30),
	Rom varchar(30),
	DaXoa bit
)
go

create table SanPham(
	MaSP int identity(1,1) primary key,
	TenSP nvarchar(100) ,
	SoLuong int,
	DonGia float,
	MoTa nvarchar(4000),
	TrangThai bit,
	MaHang varchar(30) references HangSX(MaHang),
	MaCH int references CauHinh(MaCH),
	MaLoai varchar(30) references LoaiSP(MaLoai),
	DaXoa bit
)
go

create table HinhAnh(
	MaHinh  int identity(1,1) primary key,
	MaSP int references SanPham(MaSP),
	HinhAnh varchar(50),
	DaXoa bit
)
go

create table VaiTro(
	Id varchar(20) primary key,
	TenVaiTro nvarchar(30)
)


create table KhachHang(
	TaiKhoan varchar(20) primary key,
	MatKhau varchar(20),
	HoTen nvarchar(40),
	Email varchar(40),
	SĐT varchar(15),
	DiaChi  nvarchar(200),
	DaXoa bit,
	VaiTroKH varchar(20) references VaiTro(Id),
)
go

create table NhanVien(
	TaiKhoan varchar(20) primary key,
	MatKhau varchar(20),
	HoTen nvarchar(40),
	Email varchar(20),
	SĐT varchar(15),
	DiaChi  nvarchar(200),
	NgaySinh date,
	DaXoa bit,
	VaiTroNV varchar(20) references VaiTro(Id)
)
go


create table BinhLuan(
	MaBL  int identity(1,1) primary key,
	TaiKhoan varchar(20) references KhachHang(TaiKhoan),
	MaSP int references SanPham(MaSP),
	NoiDung nvarchar(1000),
	NgayBL date
)
go


create table DonHang(
	MaDon int identity(1,1) primary key,
	MaKH varchar(20) references KhachHang(TaiKhoan),
	MaNV varchar(20) references NhanVien(TaiKhoan),
	NgayTao date,
	TongTien float,
	GhiChu nvarchar(255),
	TrangThai nvarchar(20),
	DaXoa bit
)
go


create table ChiTietDH(
	MaCTĐH int identity(1,1) primary key,
	MaDon int references DonHang(MaDon),
	MaSP int references SanPham(MaSP),
	DonGia float, 
	SoLuong int
)
go


